    private void tombolCaesarCipherActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tombolCaesarCipherActionPerformed
        String plainText = PlainText01.getText().toLowerCase();
        int key = Integer.parseInt(Key01.getText());
        String encript = CaesarEncript(plainText, key);
        CipherText01.setText(encript.toUpperCase());

    }//GEN-LAST:event_tombolCaesarCipherActionPerformed

    private void tombolCaesarPlainActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tombolCaesarPlainActionPerformed
        String encript = CipherText01.getText().toLowerCase();
        int key = Integer.parseInt(Key01.getText());
        String plainText = CaesarDecript(encript, key);
        PlainText01.setText(plainText.toUpperCase());
    }//GEN-LAST:event_tombolCaesarPlainActionPerformed

