    private void tombolVigenereDescriptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tombolVigenereDescriptActionPerformed
        String encript = CipherText02.getText().toLowerCase();
        String key = Key02.getText().toLowerCase();
        String plainText = VigenereteDectript(encript, key);
        PlainText02.setText(plainText.toUpperCase());
    }//GEN-LAST:event_tombolVigenereDescriptActionPerformed

