    private static String CaesarEncript(String text, int shift) {
        StringBuffer sb = new StringBuffer();
        for (int i = 0; i < text.length(); i++) {
            int p = text.charAt(i) - 97;//plainText
            int e = (p + shift) % 26;//hasilEncript
            char huruf = (char) (e + 97);//mengarah ke huruf sesuai angka
            sb.append(huruf);
        }
        return sb.toString();
    }

    private static String CaesarDecript(String text, int shift) {
        StringBuffer sb = new StringBuffer();
        for (int i = 0; i < text.length(); i++) {
            int e = text.charAt(i) - 97;
            int p = (e - shift) % 26;
            if (p < 0) {
                p += 26;
            }
            char huruf = (char) (p + 97);//kembali ke angka ascii
            sb.append(huruf);
        }
        return sb.toString();
    }

    private static String VigenereEncript(String text, String shift) {
        StringBuffer sb = new StringBuffer();
        int panjang = text.length();
        for (int i = 0; i < panjang; i++) {
            int j = i % shift.length();
            int k = (int) shift.charAt(j) - 97;
            int p = text.charAt(i) - 97;
            int e = (p + k) % 26;
            char huruf = (char) (e + 97);
            sb.append(huruf);

        }
        return sb.toString();
    }

    private static String VigenereteDectript(String text, String shift) {
        StringBuffer sb = new StringBuffer();
        int panjang = text.length();
        for (int i = 0; i < panjang; i++) {
            int j = i % shift.length();// j hasil modulo i, biar tidak melebihi key
            int k = (int) shift.charAt(j) - 97;
            int p = text.charAt(i) - 97;
            int e = (p - k) % 26;
            if (e < 0) {
                e += 26;

            }
            char huruf = (char) (e + 97);
            sb.append(huruf);
        }
        return sb.toString();
    }

